import React, { Component } from "react";

class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchText: "",
      placeHolder: "Veuillez saisir votre film",
    };

    handleChange()
  }
  render() {
    return <input type="text" onChange={this.handleChange} />;
  }
}
export default SearchBar;
