import React, { Component } from "react";

class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchText: "",
      placeHolder: "Veuillez saisir votre film",
    };
  }
  render() {
    return <input type="text" onChange={this.handleChange} placeholder={this.state.placeHolder} />;
  }
        //event.target.value : récupère la valeur de l'input
  handleChange(event) {
    console.log("Saisie", event.target.value);
  }
}
export default SearchBar;
